sys_script_include.script:
var CheckLoggedInTeams = Class.create();
CheckLoggedInTeams.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	checkTeamLogin: function(teamId) {
    var logInTeam = new GlideRecord('x_check_logged_in_teams');
    logInTeam.addQuery('team_id', teamId);
    logInTeam.query();
    return logInTeam.next();
    },

    logInTeam: function(teamId) {
    var logInTeam = new GlideRecord('x_check_logged_in_teams');
    logInTeam.initialize();
    logInTeam.team_id = teamId;
    logInTeam.logged_in = true;
    logInTeam.insert();
    },

    type: 'CheckLoggedInTeams'
});
