sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var gr = new GlideRecord("x_540166_apl_team_table");
	gr.query();
	while(gr.next()){
		if(gr.getValue('team_name')=='None'){
			data.image=gr.getValue('image');
		}
	}


})();
