sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;
	c.submit=function(){
		c.data.temp1=c.pname;
		c.data.temp2=c.pcategory;
		c.data.temp3=c.pnationality;
		c.data.temp4=c.active;
		c.data.temp5=c.status;
		c.data.temp6=c.basevalue;
		c.server.update();
	}
};

