sp_widget.client_script:
// Client-side script (AngularJS Controller)
api.controller = function ($scope, $http) {
  var c = this;
	c.server.update();
	// Initialize index for pagination
    c.currentPage = 0;

    // Function to show the next set of records
    c.showNext = function () {
        var pageSize = 2;
        var totalRecords = c.data.bids.length;

        if (c.currentPage < Math.ceil(totalRecords / pageSize) - 1) {
            c.currentPage++;
        }
    };

    // Function to show the previous set of records
    c.showPrevious = function () {
        if (c.currentPage > 0) {
            c.currentPage--;
        }
    };

    // Function to get the starting index for the current page
    c.getStartIndex = function () {
        var pageSize = 2;
        return c.currentPage * pageSize;
    };
};

