sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	c.server.update();

	c.placeBid = function(player) {
		var n = 500;
		c.data.newValue = parseInt(c.data.list[0].value) + parseInt("500");
		c.server.update();
		window.location.reload();
	}

};

