sys_script_include.script:
var RedirectCondition = Class.create();
RedirectCondition.prototype = {
    initialize: function() {
    },

	areAllTeamsLoggedIn: function() {
        var teamTable = new GlideRecord('x_540166_apl_apl_check_logged_in_teams');
        teamTable.query();

        var teamsTotal = new GlideRecord('x_540166_apl_team_table');
        teamsTotal.query();

        return teamTable.getRowCount() == teamsTotal.getRowCount();
    },


    type: 'RedirectCondition'
};
